func main() {
	cfg := config.NewConfig(kv.MustFromEnv())
	eth := cfg.EthClient()
	log := logan.New()

	privateKey, err := crypto.HexToECDSA(cfg.TransferConfig().Key)
	if err != nil {
		log.Fatal(err)
	}

	publicKey := privateKey.Public()
	publicKeyECDSA, ok := publicKey.(*ecdsa.PublicKey)
	if !ok {
		log.Fatal("error casting public key to ECDSA")
	}

	myAddress := crypto.PubkeyToAddress(*publicKeyECDSA)
	contractAddress := common.HexToAddress(cfg.TransferConfig().Address)
	chainID, err := eth.NetworkID(context.Background())
	if err != nil {
		log.Fatal(err)
	}
	parsed, err := abi.JSON(strings.NewReader(myABI))
	if err != nil {
		log.Fatal("failed to parse contract ABI")
	}

	var Contract = bind.NewBoundContract(
		contractAddress,
		parsed,
		eth,
		eth,
		eth,
	)

	fmt.Println(cfg.ContractConfig().AddressArray)

	path := []common.Address{common.HexToAddress("0xae13d989daC2f0dEbFf460aC112a837C89BAa7cd"), common.HexToAddress("0xeD24FC36d5Ee211Ea25A80239Fb8C4Cfd80f12Ee")}

	AmountOutMin := cfg.ContractConfig().AmountOutMin

	d := time.NewTicker(cfg.TransferConfig().Time)
	for {
		select {
		case tm := <-d.C:
			timestamp := big.NewInt(tm.Add(cfg.TransferConfig().Timestamp).Unix())

			balance, err := eth.BalanceAt(context.Background(), myAddress, nil)
			if err != nil {
				log.Fatal(err)
			}
			var functionValue big.Int
			percent := big.NewInt(cfg.ContractConfig().Percent)
			functionValue.Mul(functionValue.Div(balance, big.NewInt(100)), percent)

			_, err = Contract.Transact(&bind.TransactOpts{
				From: myAddress,
				Signer: func(fromAddress common.Address, tx1 *types.Transaction) (*types.Transaction, error) {
					signature, err := crypto.Sign(types.NewEIP155Signer(chainID).Hash(tx1).Bytes(), privateKey)
					if err != nil {
						return nil, err
					}
					return tx1.WithSignature(types.NewEIP155Signer(chainID), signature)
				},
				Value: &functionValue,
			}, "swapExactETHForTokens", &AmountOutMin, &path, &myAddress, &timestamp)
			if err != nil {
				log.WithError(err).Error("error during calling set function")
				return
			}

			log.Info("success")
		}

	}
}
